import {RuleDefinition} from '../index';

export interface CatalogImpl {
  getDocUrl(ruleDocPath: string): string;

  readonly catalogDir: string;
  readonly ruleDefinitions: RuleDefinition[];
}


export abstract class BaseCatalogImpl implements CatalogImpl {
  readonly catalogDir: string;
  readonly ruleDefinitions: RuleDefinition[] = [];

  protected constructor(catalogDir: string) {
    this.catalogDir = catalogDir;
  }

  abstract getDocUrl(ruleDocPath: string): string;
}
