import {validate, ValidationFilter} from './lib/catalog-validator';
import {FsCatalog} from './lib/fs-catalog';
import {Catalog} from './index';

export function getCatalog(dir: string): Catalog {
  return new FsCatalog(dir);
}

export function validateCatalog(dir: string, validationFilter?: ValidationFilter): void {
  return validate(new FsCatalog(dir), validationFilter);
}
