import {LintRule, Severity} from '../../../../..';

export const rule: LintRule = {
  description: 'field names should not start with "x"',
  moreInfoUrl: 'http://www.invalid-field-name.com',
  severity: Severity.Error,
  async run(ctx) {

    ctx.visit({
      visitField(field) {
        ctx.assert(!field.name.startsWith('x'), {errorAt: field});
      }
    });
  }
};
