import {LintRule, Severity} from '../../../../..';

export const rule: LintRule = {
  description: 'reports warnings about a service',
  moreInfoUrl: 'http://www.service.com',
  severity: Severity.Error,
  graceUntil: new Date('9999-01-01'),
  async run(ctx) {
    ctx.visit({
      visitService(service) {
        ctx.report({errorAt: service});
      }
    });
  }
};
