import {LintRule, Severity} from '../../../../..';

export const rule: LintRule = {
  description: 'field number 666 is invalid',
  moreInfoUrl: 'http://www.invalid-field-number.com',
  severity: Severity.Error,
  strict: true,
  async run(ctx) {
    ctx.visit({
      visitField(field) {
        ctx.assert(field.number !== 666, {errorAt: field});
      }
    });
  }
};
