import {LintRule} from '../../../../..';

export const rule: LintRule = {
  description: 'field names that start with underscore are not allowed',
  moreInfoUrl: '../js-rule/README.md',
  async run(ctx) {
    ctx.visit({
      visitField(field) {
        if (field.name.startsWith('_')) {
          ctx.report({errorAt: field});
        }
      }
    });
  }
};
