import {Field} from '@wix/proto-syntax-tree';
import {LintRule} from '../../..';

const rule: LintRule = {
  description: 'digits not allowed in field names',
  run(ctx) {
    const digit = /\d/;
    ctx.visit({
      visitField(field: Field) {
        if (field.name.match(digit)) {
          ctx.report({errorAt: field.detailedSourceCode.name});
        }
      }
    });
  }
};

export {rule};

