const rule = {
  description: 'digits not allowed in field names',
  run(ctx) {
    const digit = /\d/;
    ctx.visit({
      visitField(field) {
        if (field.name.match(digit)) {
          ctx.report({errorAt: field});
        }
      }
    });
  }
};

//intentionally an error
//eslint-disable-next-line no-undef
module.exports = {ruleXXX, rule};

