export interface CliOptions {
  plugins: string[];
}

//flynt -p=plugin-path
//returns list of plugins to load from cli parameters
export async function parseCliPluginOptions(args: string[]): Promise<CliOptions> {
  const yargs = (await import('yargs')).default;
  const cliOpts = yargs(args).options({
    plugin: {
      alias: 'p',
      type: 'array',
      string: true,
      default: [],
    }
  }).help(false).parse();
  return {plugins: cliOpts.plugin};
}
