import {parseCliPluginOptions} from '../lib/cli-options';

describe('parseCliPluginOptions', () => {
  it('loads plugins', async () => {
    const opts = await parseCliPluginOptions(['-p', './some/plugin/path', '-p', './some/other/path']);
    expect(opts.plugins).toEqual([
      './some/plugin/path',
      './some/other/path',
    ]);
  });
});
