//This rule reports an issue whenever it finds a service element
module.exports = {
  rule: {
    description: 'services are strictly not allowed',
    moreInfoUrl: 'http://www.strict-always-fail.com',
    severity: 'error',
    strict: true,
    async run(ctx) {
      ctx.visit({
        visitService(service) {
          ctx.report({errorAt: service});
        }
      });
    }
  }
};
