//This rule reports an issue whenever it finds a service element
module.exports = {
  rule: {
    description: 'services are not allowed',
    moreInfoUrl: 'http://www.always-fail.com',
    async run(ctx) {
      ctx.visit({
        visitService(service) {
          ctx.report({errorAt: service});
        }
      });
    }
  }
};
