//This rule reports an issue whenever it finds a new service element
module.exports = {
  rule: {
    description: 'new services are not allowed',
    moreInfoUrl: 'http://www.new-always-fail.com',
    stateful: true,
    async run(ctx) {
      ctx.visitAdded({
        visitService(service) {
          ctx.report({errorAt: service});
        }
      });
    }
  }
};
