export interface LocaleDataset {
  countries: Country[];
  locales: Locale[];
  timezones: Timezone[];
  currencies: Currency[];
  demoAddresses: DemoAddress[];
  supportedLanguages: Language[];
  addressPartDisplayNames: AddressParts;
  deprecationTimestamps: DeprecationTimestamp[];
}

export interface Country {
  key: string;
  shortKey?: string;
  displayName: string;
  dialCode: string;
  subdivisions?: Subdivisions;
  properties?: {
    [key: string]: any;
  };
  addressPattern?: string;
  addressPartDisplayNames: Partial<AddressParts>;
}

export interface Subdivisions {
  type: string;
  list: Subdivision[];
  displayName: string;
}

export interface Subdivision extends Replaceable {
  key: string;
  displayName: string;
}

export interface Locale {
  key: string;
  displayName: string;
}

export interface Timezone {
  key: string;
  displayName: string;
}

export interface Currency {
  key: string;
  displayName: string;
  symbol: string;
  fraction: number;
  override?: {
    symbol: string;
    countryCodes: string[];
  };
}

export interface DemoAddress {
  key: string;
  fieldsDisplayNames: AddressParts;
}

export interface AddressParts {
  country?: string;
  subdivision?: string;
  addressLine1?: string;
  addressLine2?: string;
  city?: string;
  zipCode?: string;
  firstName?: string;
  lastName?: string;
  company?: string;
  phone?: string;
  taxInfo?: TaxInfo;
  streetName?: string;
  streetNumber?: string;
}

export interface TaxInfo {
  type: TaxType;
  id: string;
}

export type TaxType = 'CPF' | 'CNPJ';

export interface Language {
  key: string;
  displayName: string;
}

export interface Replaceable {
  replaces?: string[];
  replacedBy?: string;
  deprecatedOn?: number;
}

export interface DeprecationTimestamp {
  timestamp: number;
  description: string;
}
