import {Type, TypeName} from '../api';

export function fullyQualifiedName(parent: Type | null, name: string) {
  if (parent) {
    if (parent.typeName === TypeName.File) {
      if (parent.package) {
        return `.${parent.package}.${name}`;
      }
      return `.${name}`;
    }
    return `${parent.fqn}.${name}`;
  }
  return name;
}
