import generated from '../lib/generated/proto-generated';
import {GrpcStatusError, GrpcStatus} from '..';

interface GrpcValidationError extends GrpcStatusError {
  readonly validationError: generated.wix.api.ValidationError;
}

interface GrpcApplicationError extends GrpcStatusError {
  readonly applicationError: generated.wix.api.ApplicationError;
}

export function isGrpcStatusError(err: any, expectedStatus?: GrpcStatus | GrpcStatus[]): err is GrpcStatusError;
export function isValidationError(err: any): err is GrpcValidationError;
export function isApplicationError(err: any, expectedErrorCode?: string | string[]): err is GrpcApplicationError;