#!/usr/bin/env node

const wixProtoCodegen = require('./lib/wix-proto-codegen'),
  asOpts = require('./lib/cli-opts');

wixProtoCodegen(asOpts(process.argv))
  .then(() => console.log('code generation completed.'))
  .catch(err => {
    console.error('code generation failed: ', err.message);
    process.exit(1);
  });




