const {create} = require('./module'),
  {generate} = require('escodegen');

function generateJavaScript(protobufAst, opts) {
  return generateAndFormat(protobufAst, opts);
}

function generateAndFormat(ast, opts) {
  return generate(create(ast, opts), {comment: true, format: {indent: {style: '  '}}});
}

module.exports = {generateJavaScript};
