const e = require('estree-builder'),
  {id} = require('./util');

const long = (stringLongs) => stringLongs ? e.string('0') : id('Long.ZERO');
const number = () => e.number(0);
const string = () => e.string('');
const boolean = () => e.false();

const DEFAULTS = {
  'int32': number,
  'sint32': number,
  'uint32': number,
  'fixed32': number,
  'sfixed32': number,
  'double': number,
  'float': number,
  'int64': long,
  'uint64': long,
  'sint64': long,
  'fixed64': long,
  'sfixed64': long,
  'bool': boolean,
  'string': string,
  'bytes': string,
};

module.exports = {DEFAULTS};
