const {sep} = require('path');

function protoFileName(filePath) {
  const separator = filePath.includes('node_modules') ? 'node_modules' : 'proto';
  return stripPath(filePath, separator);
}

function stripPath(filePath, folderName) {
  const [path] = filePath.split(folderName + sep).slice(-1);
  return path;
}

module.exports = {protoFileName};
