const path = require('path');
const protobuf = require('@wix/wnp-protobufjs');
  
const DESCRIPTOR = protobuf.loadSync(path.resolve(__dirname, path.join('..', '..', 'proto', 'google', 'protobuf', 'descriptor.proto')));

function isFromDescriptor(typeName) {
  return !!DESCRIPTOR.lookup(typeName); 
}

module.exports = {isFromDescriptor};