function generate(w, namespace) {

  w.write('export interface Any')
    .block(() => {
      w.writeLine('readonly typeUrl: string;');
      w.writeLine('readonly typeName: string;');
      w.writeLine('is(ctor: $$GeneratedMessage$$<any>): boolean;');
      w.writeLine('unpack<S>(ctor: $$GeneratedMessage$$<S>): S;');
    });

  w.write('export namespace Any')
    .block(() => {
      w.writeLine(`export function pack<S>(ctor: $$GeneratedMessage$$<S>, value: S): ${namespace}.google.protobuf.Any;`);
      w.writeLine('export function isAny(instance: object): boolean;');
      w.writeLine('export function typeUrl(ctor: $$GeneratedMessage$$<any>): string;');
    });
}

module.exports = generate;
