module.exports = {
  messages: {
    'binary.base': '\'{field}\' value must be an instance of Buffer',
  },
  alias: 'binary',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (!Buffer.isBuffer(value)) {
        errors.push({type: 'binary.base'});
      }
      return value;
    }
  }
}; 