const { colorHexRegex } = require('@wix/wix-js-validations');

module.exports = {
  messages: {
    'string.colorHex': '\'{field}\' value must be a hexadecimal color code',
  },
  alias: 'colorHex',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!colorHexRegex.test(value)) {
        errors.push({type: 'string.colorHex'});
      }
      return value;
    }
  }
};