const {COUNTRY_CODES} = require('../i18n');

module.exports = {
  messages: {
    'string.country': '\'{field}\' value \'{actual}\' must be a 2-letter ISO country code',
  },
  alias: 'country',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!COUNTRY_CODES.has(value.toLowerCase())) {
        errors.push({type: 'string.country', actual: value});
      }
      return value;
    }
  }
}; 