const {CURRENCY_CODES} = require('../i18n');

module.exports = {
  messages: {
    'string.currency': '\'{field}\' value \'{actual}\' is not a 3-letter ISO currency code',
  },
  alias: 'currencyCode',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!CURRENCY_CODES.has(value.toLowerCase())) {
        errors.push({type: 'string.currency', actual: value});
      }
      return value;
    }
  }
};