const { GUIDRegex } = require('@wix/wix-js-validations');

module.exports = {
  messages: {
    'string.guid': '\'{field}\' value must be a valid GUID',
  },
  alias: 'guid',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!GUIDRegex.test(value)) {
        errors.push({type: 'string.guid'});
      }
      return value;
    }
  }
};