const {isDomain} = require('node-uri');

module.exports = {
  messages: {
    'string.hostname': '\'{field}\' value must a valid hostname',
  },
  alias: 'hostname',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!isDomain(value)) {
        errors.push({type: 'string.hostname'});
      }
      return value;
    }
  }
}; 