const {isIPv4} = require('node-uri');

module.exports = {
  messages: {
    'string.ipv4': '\'{field}\' value must a valid IPv4 address',
  },
  alias: 'ipv4',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!isIPv4(value)) {
        errors.push({type: 'string.ipv4'});
      }
      return value;
    }
  }
}; 