const {isIPv6} = require('node-uri');

module.exports = {
  messages: {
    'string.ipv6': '\'{field}\' value must a valid IPv6 address',
  },
  alias: 'ipv6',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!isIPv6(value)) {
        errors.push({type: 'string.ipv6'});
      }
      return value;
    }
  }
}; 