const {isValidLanguageTag} = require('../i18n');

module.exports = {
  messages: {
    'string.languageTag': '\'{field}\' value must be a valid IETF BCP 47 language tag',
  },
  alias: 'languageTag',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!isValidLanguageTag(value)) {
        errors.push({type: 'string.languageTag'});
      }
      return value;
    }
  }
}; 