const { phoneRegex } = require('@wix/wix-js-validations');

module.exports = {
  messages: {
    'string.phone': '\'{field}\' value must a valid phone number',
  },
  alias: 'phone',
  schema: {
    type: 'custom',
    optional: true,
    check(value, errors) {
      if (typeof value !== 'string') {
        errors.push({type: 'string', actual: value});
      } else if (!phoneRegex.test(value)) {
        errors.push({type: 'string.phone'});
      }
      return value;
    }
  }
};